class Solution {
    func validMountainArray(_ A: [Int]) -> Bool {
        guard A.count >= 3 else {
          return false
        }
      
        let middleIndex = (A.count / 2)
        var valleyIndex = middleIndex
       // print("Middle Index = \(middleIndex)")
       // print("Valley Index = \(valleyIndex)")
        if A[middleIndex - 1] < A[middleIndex] {
          //print("Entered first valley check")
          if A[middleIndex] > A[middleIndex + 1] {
           // print("Entered second valley check")
            return (checkStrictlyIncreasing(A, 0, middleIndex - 1)) && (checkStrictlyDecreasing(A, middleIndex + 1, A.count - 1))
          } else {
           valleyIndex = middleIndex + 1
           while (valleyIndex < A.count - 1) {
             if (A[valleyIndex] > A[valleyIndex + 1]) {
               return (checkStrictlyIncreasing(A, 0, valleyIndex - 1) && checkStrictlyDecreasing(A, valleyIndex + 1, A.count - 1))
             } else {
               valleyIndex += 1
             }
           }
         //  print("Reached End of while")
           return false 
          }
        } else {
          valleyIndex = middleIndex - 1
          print(valleyIndex)
          while (valleyIndex > 0) {
             if (A[valleyIndex] > A[valleyIndex - 1]) {
               return (checkStrictlyIncreasing(A, 0, valleyIndex - 1) && checkStrictlyDecreasing(A, valleyIndex + 1, A.count - 1))
             } else {
               valleyIndex -= 1
             }
           }
        //  print("Reached End of while")
          return false
        }
      
    }
  
  func checkStrictlyIncreasing(_ A: [Int], _ startIndex: Int, _ endIndex: Int) -> Bool {
   // print("Entered checkStrictlyIncreasing")
    if startIndex == endIndex {
      return true
    }
    
    for i in (startIndex + 1)...endIndex {
      
      if A[i] == A[i - 1] {
        return false
      }
      if A[i] < A[i - 1] {
        return false
      }
    }
    return true
  }
  
  func checkStrictlyDecreasing(_ A: [Int], _ startIndex: Int, _ endIndex: Int) -> Bool {
   // print("Entered checkStrictlyDecreasing")
    if startIndex == endIndex {
      return true
    }
    for i in (startIndex + 1)...endIndex {
       if A[i] == A[i - 1] {
        return false
      }
      if A[i] > A[i - 1] {
        return false
      }
    }
    return true
  }
}

